mapper.select:
select A.IdActivo as codigo ,A.Nombre as nombre ,A.MontoCompra as montoCompra ,A.IdProveedor as idProveedor ,A.IdTipoMoneda as tipoMoneda ,A.IdTipoActivo as idTipoActivo ,A.CodigoPatrimonial as codigoPatrimonial ,A.NumeroActivo as numeroSerie ,A.Descripcion as descripcion ,A.IdLocal as idLocal ,A.Estado as estado ,A.IdMarca as idMarca ,A.BajoMantenimiento as bajoMantenimiento from activo A inner join tipoactivo TA on A.IdTipoActivo=TA.IdTipoActivo
TA.Nombre like #{nombre}
AND A.NumeroActivo like #{numeroSerie}
AND A.Estado=#{estado}
AND A.IdLocal=#{idLocal}

select IA.IdInventario as idInventario ,IA.IdTipoActivo as idTipoActivo ,IA.IdLocal as idLocal ,IA.Cantidad as cantidad from inventario_activo IA
IA.IdTipoActivo = #{tipoActivo}
AND IA.IdLocal = #{idLocal}

select IA.IdInventario as idInventario ,IA.IdTipoActivo as idTipoActivo ,IA.IdLocal as idLocal ,IA.Cantidad as cantidad from inventario_activo IA
IA.IdLocal = #{idLocal}
AND IA.Cantidad!=0

select A.IdActivo as codigo ,A.IdTipoActivo as tipoActivo ,A.IdLocal as idLocal ,A.FechaVencimientoGarantia as fechaVencimientoGarantia ,A.IdMarca as idMarca ,A.IdProveedor as idProveedor ,A.IdTipoActivo as idTipoActivo ,A.Estado as estado ,A.NumeroActivo as numeroSerie from activo A
A.IdLocal = #{idLocal}
AND A.IdTipoActivo = #{tipoActivo}
AND A.Estado!=8

select A.IdActivo as codigo ,A.Nombre as nombre ,A.MontoCompra as montoCompra ,A.IdProveedor as idProveedor ,A.IdTipoMoneda as tipoMoneda ,A.IdTipoActivo as idTipoActivo ,A.NumeroActivo as numeroSerie ,A.Descripcion as descripcion ,A.Estado as estado ,A.BajoMantenimiento as bajoMantenimiento ,A.IdLocal as idLocal from activo A
A.Nombre like #{nombre}
AND A.NumeroActivo like #{numeroSerie}
AND A.Estado=#{estado}
AND A.BajoMantenimiento=#{bajoMantenimiento}
AND A.IdLocal=#{idLocal}

select A.IdActivo as codigo ,A.Nombre as nombre ,A.MontoCompra as montoCompra ,A.IdProveedor as idProveedor ,A.IdTipoMoneda as tipoMoneda ,A.IdTipoActivo as idTipoActivo ,A.NumeroActivo as numeroSerie ,A.Descripcion as descripcion ,A.Estado as estado ,A.BajoMantenimiento as bajoMantenimiento ,A.IdLocal as idLocal from activo A
A.Nombre like #{nombre}
AND A.NumeroActivo like #{numeroSerie}
AND A.Estado=#{estado}
AND A.IdLocal=#{idLocal}
AND A.IdActivoPadre IS NULL

mapper.update:
update subproceso set subproceso.Nombre=#{nombre}
 ,subproceso.FechaActual=#{fechaActual}
 ,subproceso.Descripcion=#{descripcion}
 ,subproceso.Estado=#{estado}
 where subproceso.IdSubProceso= #{codigo}

update activo set activo.MotivoBaja=#{motivoBaja}
 ,activo.Estado=#{estado}
 ,activo.FechaBaja=#{fechaBaja}
 where activo.IdActivo= #{codigo}

update activo set
 activo.NumeroActivo=#{numeroSerie}
 ,activo.CodigoPatrimonial=#{codigoPatrimonial}
 ,activo.IdTipoActivo=#{tipoActivo}
 ,activo.IdLocal=#{idLocal}
 ,activo.IdProveedor=#{proveedor}
 ,activo.IdMarca=#{idMarca}
 ,activo.MontoCompra=#{montoCompra}
 ,activo.IdTipoMoneda=#{tipoMoneda}
 ,activo.FechaCompra=#{fechaCompra}
 ,activo.FechaRegistro=#{fechaRegistro}
 where activo.IdActivo= #{codigo}

mapper.select:
select COALESCE(count(*),0)
FROM h_programacionejecutada
where IdProgramacion = #{idProgramacion}
